    <div className="app-layout">
      <div>
        <Header />
      </div>

      <div className="sidebar-main-container">
        <div>
          <Sidebar />
        </div>

        <div className="main-content">
          <Switch>
            <Route path={"/"} exact>
              <Dashboard />
            </Route>
