import { useEffect, useState } from "react";
import "./Dashboard.scss";
import axios from "axios";

const Dashboard: React.FC = () => {
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [productCount, setProductCount] = useState<number>(0);
  const [error, setError] = useState<string | null>(null);
  useEffect(() => {
    const fetchProductCount = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(
          "http://localhost:8080/products/count"
        );
        setProductCount(response.data);
      } catch (error) {
        setError((error as Error).message);
      } finally {
        setIsLoading(false);
      }
    };
    fetchProductCount();
  }, []);

  if(isLoading) {
    return <p>Loading...</p>
  }

  if(error) {
    return <p>Error Occurred: {error}</p>
  }
  return (
    <div>
      <div className="box-containers">
        <div className="box">
          <p className="box-title">Products</p>
          <p className="box-count">{productCount}</p>
        </div>

        <div className="box">
          <p className="box-title">Orders</p>
          <p className="box-count">count</p>
        </div>

        <div className="box">
          <p className="box-title">Users</p>
          <p className="box-count">count</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
