import { useState } from "react";
import "./AddUser.scss";
import { User as UserInterface } from "../../types/User";
import axios from "axios";

const AddUser: React.FC = () => {
  const emptyUser: UserInterface = {
    userId: "",
    userName: "",
    email: "",
    phoneNo: "",
    addressIds: [],
  };
  const [user, setUser] = useState<UserInterface>(emptyUser);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    setError("");
  };

  const phoneNoPattern = /^[6-9]\d{9}$/;

  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState<boolean>(false);

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (
      user.userName.trim() === "" ||
      user.email.trim() === "" ||
      !phoneNoPattern.test(user.phoneNo)
    ) {
      setError("*All fields are required & must be valid");
      return;
    }
    setIsLoading(true);
    try {
      const newUser: UserInterface = emptyUser;
      const response = await axios.post("http://localhost:8080/users", newUser);
      setUser(emptyUser);
      console.log("User added successfully: " + response.data);
    } catch (error) {
      setError((error as Error).message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="add-container">
      <div>
        <p className="heading centered">Add User</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="input-layout mb-2">
          <label>Name: </label>
          <input
            type="text"
            name="userName"
            placeholder="enter user name"
            onChange={handleChange}
            className="input-field"
          />
        </div>

        <div className="input-layout mb-2">
          <label>Email: </label>
          <input
            type="text"
            name="email"
            placeholder="enter email"
            onChange={handleChange}
            className="input-field"
          />
        </div>

        <div className="input-layout mb-2">
          <label>PhoneNo: </label>
          <input
            type="text"
            name="phoneNo"
            placeholder="enter phoneNo"
            onChange={handleChange}
            className="input-field"
          />
        </div>
        <div className="centered">
          <button className="btn btn-success">
            {isLoading ? "Adding" : "Add User"}
          </button>
        </div>
        <p className="centered error-text mt-2">{error}</p>
      </form>
    </div>
  );
};

export default AddUser;
