import { useState } from "react";
import {
  Address as AddressInterface,
  User as UserInterface,
} from "../../types/User";
import "./UserDetails.scss";

interface UserDetailsProps {
  user: UserInterface;
}

const UserDetails: React.FC<UserDetailsProps> = ({ user }) => {
  const [error, setError] = useState<string | null>(null);
  const emptyAddress: AddressInterface = {
    addressId: "",
    doorNo: "",
    street: "",
    city: "",
  };
  const [address, setAddress] = useState<AddressInterface>(emptyAddress);
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setAddress({ ...address, [e.target.name]: e.target.value });
    setError("");
  };

  const [isLoading, setIsLoading] = useState<boolean>(false);
  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if(address.doorNo.trim() === "" || address.street.trim() === "" || address.city.trim() === "") {
        setError("*All fields are required");
        return;
    }

  };
  return (
    <div>
      <div>
        <p className="user-details-name">{user.userName}</p>
        <p className="user-details">{user.email}</p>
        <p className="user-details">{user.phoneNo}</p>
      </div>

      <div className="add-container">
        <div>
          <p className="heading centered">Add Address</p>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="input-layout mb-2">
            <label>Door No: </label>
            <input
              type="text"
              name="doorNo"
              placeholder="enter doorNo"
              onChange={handleChange}
              className="input-field"
            />
          </div>

          <div className="input-layout mb-2">
            <label>Street: </label>
            <input
              type="text"
              name="street"
              placeholder="enter street"
              onChange={handleChange}
              className="input-field"
            />
          </div>

          <div className="input-layout mb-2">
            <label>City: </label>
            <input
              type="text"
              name="city"
              placeholder="enter city"
              onChange={handleChange}
              className="input-field"
            />
          </div>
          <div className="centered">
            <button className="btn btn-success">Add Address</button>
          </div>
          <p className="centered error-text mt-2">{error}</p>
        </form>
      </div>
    </div>
  );
};

export default UserDetails;
