import { useState } from "react";
import { Product } from "../../types/Product";
import axios from "axios";

const AddProduct: React.FC = () => {
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<any>(null);

  const emptyProduct: Product = {
    prodId: "",
    prodName: "",
    category: "",
    description: "",
    price: 0.0,
    imageURL: "",
  };

  const [product, setProduct] = useState<Product>(emptyProduct);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handlePrice = (e: React.ChangeEvent<HTMLInputElement>) => {
    setProduct({ ...product, [e.target.name]: +e.target.value });
  };

  const handleImage = (e: React.ChangeEvent<HTMLInputElement>) => {
    setProduct({...product, [e.target.name]: e.target.files?.[0] || null})
  }

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);
    try {
      const formData = new FormData();

      const response = await axios.post(
        "http://localhost:8080/products",
        newProduct
      );
      setProduct(emptyProduct);
      console.log("Product added successfully");
    } catch (error) {
      setError(error as Error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" name="prodName" onChange={handleChange} />
        <input type="text" name="category" onChange={handleChange} />
        <input type="text" name="description" onChange={handleChange} />
        <input type="number" name="price" onChange={handlePrice} />
        <input type="file" name="imageURL" onChange={handleImage} />
        <button>{isLoading ? "Adding" : "Add Product"}</button>
        {error && <p>{error}</p>}
      </form>
    </div>
  );
};

export default AddProduct;
